import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:get/get.dart';

class OrderQRInfo extends StatelessWidget {
  OrderQRInfo({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    Size size = MediaQuery.of(context).size;
    var qrCodeData =
        '';
    return Padding(
      padding: const EdgeInsets.all(16.0),
      child: Stack(
        children: <Widget>[
          Column(
            crossAxisAlignment: CrossAxisAlignment.center,
            children: <Widget>[
              IntrinsicHeight(
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: <Widget>[
                    VerticalDivider(
                      thickness: 1.5,
                      endIndent: 4,
                      indent: 4,
                      color: Colors.grey.withOpacity(0.5),
                    ),
                    // Separate the timer to avoid rebuilding the QR code
                    // Text(
                    //   '0s',
                    //   style: TextStyle(color: Colors.green),
                    // )
                  ],
                ),
              ),
              Center(
                child: GestureDetector(
                  onTap: () {
                    // Reload the QR code if expired
                    // if (controller.remainingTime.value <= 0) {
                    //   controller.resumeQRCodeTimer(); // Fetch new QR code
                    // }
                  },
                  child: Stack(
                    alignment: Alignment.center,
                    children: <Widget>[
                      Container(
                          width: size.width * 0.55,
                          height: size.width * 0.55,
                          decoration: BoxDecoration(
                            borderRadius: BorderRadius.circular(10),
                            color: Colors.transparent,
                          ),
                          child: qrCodeData.isNotEmpty
                              ? Image.memory(
                                  base64Decode(qrCodeData),
                                  fit: BoxFit.cover,
                                  errorBuilder: (context, error, stackTrace) {
                                    return Container(
                                      height: size.width * 0.55 + 5,
                                      width: size.width * 0.55 + 5,
                                      decoration: BoxDecoration(
                                        borderRadius: BorderRadius.circular(10),
                                        color: Colors.black.withOpacity(0.7),
                                      ),
                                      child: Center(
                                        child: Text(
                                          'Li ti m QR',
                                          style: TextStyle(
                                              color: Colors.white,
                                              fontSize: 14),
                                        ),
                                      ),
                                    );
                                  },
                                )
                              : SizedBox())
                    ],
                  ),
                ),
              ),
            ],
          ),
        ],
      ),
    );
  }
}
